import './App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';

function App() {

  // API Connection
  const urlAPI = "https://localhost:44384/api/Products";

  const data = [data, setData] = useState([]);

  const requestGet = async() =>{
      await axios.get(urlAPI)
      .then(response => {
        setData(response.data);
      }).catch(error => {
        console.log(error);
      });
  }

  useEffect(() => {
    requestGet();
  })


  return (
    <div className="App">
        <div className="container">
            <h1>Products</h1>
            <button className="btn createNew">Create New Product</button>
            <table>
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Name</th>
                  <th>Description</th>
                  <th>Price</th>
                  <th>Options</th>
                </tr>
              </thead>
              <tbody>
                {data.map(product => {
                  <tr key={product.id}>
                    <td>{product.id}</td>
                    <td>{product.name}</td>
                    <td>{product.description}</td>
                    <td>Griffin</td>
                    <td>
                        <button className="btn edit-btn">Edit</button>
                        <button className="btn delete-btn">Delete</button>
                    </td>
                </tr>
                })}
              </tbody>
            </table>
        </div>
    </div>
  );
}

export default App;
